import nltk
import random

def preprocess_data(data):
    processed_data = []
    for question, answer in data:
        tokens = set(nltk.word_tokenize(question.lower()))
        processed_data.append((tokens, question, answer))
    return processed_data

def find_best_match(user_input, data):
    user_tokens = set(nltk.word_tokenize(user_input.lower()))
    best_match = None
    highest_similarity = 0
    
    for tokens, question, answer in data:
        similarity_score = len(user_tokens.intersection(tokens))
        if similarity_score > highest_similarity:
            highest_similarity = similarity_score
            best_match = (question, answer)
    
    return best_match
def get_reply(user_input, data):
    best_match = find_best_match(user_input, data)
    
    if best_match is None:
        return "I'm sorry, I don't understand. Can you please rephrase or provide more information?"
    return best_match[1]
processed_patterns = preprocess_data(patterns)




