import tkinter as tk
from tkinter import scrolledtext

def handle_input(event=None):
    user_input = user_entry.get()
    chat_area.config(state='normal')
    chat_area.insert(tk.END, f"You: {user_input}\n")
    response = get_reply(user_input, processed_patterns) 
    chat_area.insert(tk.END, f"HappyChat: {response}\n")
    chat_area.config(state='disabled')
    user_entry.delete(0, tk.END)
    if user_input.lower() == 'bye':
        root.destroy()


def clear_default_text(event):
    default_text = "Type your message here"
    if user_entry.get() == default_text:
        user_entry.delete(0, tk.END)


root = tk.Tk()
root.title("HappyChat")

background_color = "#6495ED"  
root.configure(bg=background_color)


chat_frame = tk.Frame(root, bg="#e6e6e6")
chat_frame.place(relwidth=0.75, relheight=0.8, relx=0.1, rely=0.1)


chat_area = scrolledtext.ScrolledText(chat_frame, wrap=tk.WORD, bg="#ffffff", fg="#000000", state='disabled')
chat_area.pack(padx=10, pady=10, fill=tk.BOTH, expand=True)
chat_area.config(state='normal')
chat_area.insert(tk.END, "HappyChat: Welcome to HappyChat! I am a mental health Chatbot. Is there anything you would like to share with me?\n")
chat_area.config(state='disabled')

default_text = "Type your message here"
user_entry = tk.Entry(root, bg="#d3d3d3", fg="#000000", font=("Arial", 12))
user_entry.place(relwidth=0.6, relheight=0.05, relx=0.1, rely=0.9)
user_entry.insert(0, default_text)  # Insert default text
user_entry.bind("<Key>", clear_default_text)  # Bind event to clear default text
user_entry.bind("<Return>", handle_input)


send_button = tk.Button(root, text="Send", bg="#4CAF50", fg="#ffffff", command=handle_input)
send_button.place(relwidth=0.15, relheight=0.05, relx=0.72, rely=0.9)

watermark_label = tk.Label(root, text="Your Path to Wellness Starts with a HappyChat", bg=background_color, fg="#ffffff", font=("Arial", 20, "italic"))
watermark_label.place(relx=0.5, rely=0.5, anchor=tk.CENTER)

root.mainloop()


